 * albedoTex.addressV = 'repeat';
 * albedoTex.filterMin = 'linear';
 * albedoTex.filterMag = 'linear';
 *
 * // Sample the texture
 * const uv = new VertexUVNode();
 * const sample = new TextureSampleNode();
 * sample.connectInput(1, albedoTex, 1);
